        [Test]
        public void check_exception_when_filename_null_or_empty()
        {
            Assert.Throws<FileNotFoundException>(() => LongWalk.ReadFile(null));
            Assert.Throws<FileNotFoundException>(() => LongWalk.ReadFile(""));
        }
        [Test]
        public void check_exception_when_filecontent_empty()
        {
            var input = LongWalk.ReadFile(@"C:\Users\Empty.txt");
            Assert.Throws<InvalidDataException>(() => LongWalk.ParseInput(input));
        }
    }
